mapper.select:
		SELECT 	TO_CHAR(FC_DATE, 'YYYY-MM-DD') AS FC_DATE, LOCAL_NAME, RAINFALL_PROB, RAIN_PREC, 
				MIN_TEMPERATURE, MAX_TEMPERATURE, AVG_TEMPERATURE, FINE_DUST, PARTICULATE_DUST, 
				WIND_SP, WIND_DR, TEMP_0, TEMP_3, TEMP_6, TEMP_9, TEMP_12, TEMP_15, TEMP_18, TEMP_21
		FROM 	WEATHER
		WHERE 	TO_CHAR(FC_DATE, 'YYYY-MM-DD') = #{fc_date}

    	AND LOCAL_NAME = #{local_name}

mapper.update:
UPDATE 	WEATHER
SET  	RAINFALL_PROB = #{rainfall_prob},
		RAIN_PREC = #{rain_prec}, 
		MIN_TEMPERATURE = #{min_temperature}, 
		MAX_TEMPERATURE = #{max_temperature}, 
		AVG_TEMPERATURE = #{avg_temperature}
WHERE 	TO_CHAR(FC_DATE, 'YYYY-MM-DD') = #{fc_date}
AND 	LOCAL_NAME = #{local_name}

mapper.delete:
DELETE 	WEATHER
WHERE 	TO_CHAR(FC_DATE, 'YYYY-MM-DD') = #{fc_date}
AND 	LOCAL_NAME = #{local_name}
